mapper.select:
select * from COMMENT
where boardId = #{boardId}
order by inserted desc

mapper.update:
update COMMENT
set
    content = #{content}
where
    boardId = #{boardId}
        and
    writer = #{writer}
        and
    id = #{commentId}

mapper.delete:
delete from COMMENT
where
    boardId = #{boardId}
        and
    writer = #{writer}
        and
    id = #{commentId}
