mapper.update:
update MEMBERSHIP
set
    endDate = #{endDate},
    remainingPT = #{remainingPT}
where
    memberId = #{memberId}

update MEMBER
set
    centerId = #{centerId},
    trainerId = #{trainerId}
where
    id = #{memberId}

mapper.select:
select t.id, m.name from TRAINER t
                             join MEMBER m on t.memberId = m.id
where t.centerId = #{centerId}
